    name: string;
    traits: string[];
    humorStyle: 'witty' | 'sarcastic' | 'friendly';
    expertise: string[];
  };

  async function generateResponse(context: ConversationContext): Promise<string> {
    const { message, groupContext, userHistory } = context;

    // An√°lise do contexto e tom da conversa
    const conversationTone = analyzeTone(message);
    const groupMood = analyzeGroupMood(groupContext);

